#include "quadrato.h"quadrato::quadrato(int lato){    v=NULL;    v=new int;    *v=lato;}int quadrato::perimetro(int a){  this->a=*(v)*4;return a;}int  quadrato:: area(int b){    this->b=*(v)**(v);    return b;}float  quadrato::diagonale(float c){ this->c=*(v)*sqrt(2); return c;}void quadrato::stampa() {    fprintf(stdout, " %d  %d  %f  %s",a, b, c, v);}void quadrato::file() {    p=fopen( "cronologia.txt", "a");    fprintf(p," %d  %d  %f  %s", a, b, c, v);    fclose(p);}